var st = 'on'
$(".box-header").click(function(){
    if(st == 'off'){
        var el = document.querySelector('.cart')
        el.classList.remove('slide-show') 
        el.classList.add('slide-hidden') 
        document.querySelector('.cart .box-header').style="background-color: #BD134C"
        st = 'on'
    }
    else{
        var el = document.querySelector('.cart')
        el.classList.remove('slide-hidden') 
        el.classList.add('slide-show') 
        document.querySelector('.cart .box-header').style="background-color: #840731;"
        st = 'off'
    }
});
